import React, { useState, useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import axios from "axios";

const Product = () => {
  const [product, setProduct] = useState({
    id: "",
    product_name: "",
    product_price: "",
    product_status: "",  
    dvt: "",
    amount: ""
  });
  const { id } = useParams();
  useEffect(() => {
    loadProduct();
  }, []);
  const loadProduct = async () => {
    const res = await axios.get(`http://localhost:9000/products/${id}`);
    setProduct(res.data);
  };
  return (
    <div className="container py-4">
      <Link className="btn btn-primary" to="/">
        Back to Home
      </Link>
      <h1 className="display-4">Product Id: {id}</h1>
      <hr />
      <ul className="list-group w-50">
        <li className="list-group-item">name: {product.name}</li>
        <li className="list-group-item">product name: {product.productname}</li>
        <li className="list-group-item">email: {product.email}</li>
        <li className="list-group-item">phone: {product.phone}</li>
        <li className="list-group-item">website: {product.website}</li>
      </ul>
    </div>
  );
};

export default User;
