import React, { useState } from "react";
import axios from 'axios'
import { useHistory, useParams } from "react-router-dom";


function ExportProduct() {
    const url = "http://localhost:9000/products/create-paint-export"
  let history = useHistory();
  const { id } = useParams();

  const [productExport, setProductExport] = useState({

    amount: ""
  });







  const { amount } = product;
  const onInputChange = e => {
    setProductExport({ ...productExport, id, [e.target.name]: e.target.value });
  };

  const onSubmit = async e => {
    e.preventDefault();
    await axios.post(url, productExport);
    history.push("/");
  };
  return (
    <div className="container">
      <div className="w-75 mx-auto shadow p-5">
        <h2 className="text-center mb-4">Thêm sản phẩm sơn</h2>
        <form onSubmit={e => onSubmit(e)}>
          <div className="form-group">
            <input
              type="text"
              className="form-control form-control-lg"
              placeholder="Nhập tên sản phẩm"
              name="product_name"
              value={product_name}
              onChange={e => onInputChange(e)}
            />
          </div>

          <button className="btn btn-primary btn-block">Thêm</button>
        </form>
      </div>
    </div>
  );
}

export default ExportProduct;
