import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const Home = () => {
  const [products, setProduct] = useState([]);
  const url = "http://localhost:9000/products"

  useEffect(() => {
    loadProduct();
  }, []);

  const loadProduct = async () => {
    const result = await axios.get(url);
    setProduct(result.data.reverse());
  };

  const deleteProduct = async id => {
    await axios.delete(`url/${id}`);
    loadProduct();
  };

  return (
    <div className="container">
      <div className="py-4">
        <h1>Tồn kho</h1>
        <table className="table border shadow">
          <thead className="thead-dark">
            <tr>
              <th scope="col"># Id</th>
              <th scope="col">Tên sản phẩm</th>
              <th scope="col">Giá mỗi sản phẩm</th>
              <th scope="col">Tình trạng sản phẩm</th>
              <th scope="col">Đơn vị tính</th>
              <th scope="col">Số lượng sản phẩm</th>
              <th scope="col">Action</th>
              <th scope="col">Xuất hàng</th>
            </tr>
          </thead>
          <tbody>
            {products.map((product, index) => (
              <tr>
                <th scope="row">{index + 1}</th>
                <td>{product.product_name}</td>
                <td>{product.product_price}</td>
                <td>{product.product_status}</td>
                <td>{product.dvt}</td>
                <td>{product.amount}</td>

                <td>
                  <Link class="btn btn-primary mr-2" to={`/products/${product.id}`}>
                    View
                  </Link>
                  <Link
                    class="btn btn-outline-primary mr-2"
                    to={`/users/edit/${product.id}`}
                  >
                    Edit
                  </Link>
                  <Link
                    class="btn btn-danger"
                    onClick={() => deleteProduct(product.id)}
                  >
                    Delete
                  </Link>

                  <Link class="btn btn-light">
                    Xuất hàng
                  </Link>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Home;
